INSERT INTO hotels (name, imgURL, favorite, description, webURL, address, phoneNumber) VALUES ("Havana","https://images.all-free-download.com/images/graphicthumb/beach_beautiful_boat_holiday_hotel_island_lake_600931.jpg", 0,"The PREMIER Havana Nha Trang Hotel is centrally located, overlooking Nha Trang Bay. There is plenty to keep you occupied while visiting us. Whether you are planning a romantic getaway or a family vacation, there is something for everyone. The hotel is easily accessible, just 40 minutes from Cam Ranh Airport and ten minutes from Nha Trang Railway Station.","https://www.tripadvisor.ca/Hotel_Review-g293928-d4328685-Reviews-Havana_Nha_Trang_Hotel-Nha_Trang_Khanh_Hoa_Province.html","38 Tran Phu Street Loc Tho Ward, Nha Trang Vietnam","1 (855) 821-4553");
